INSERT INTO `movie_project`.`movie` (`id`, `aired_year`, `description`, `duration`, `poster`, `name`, `quality`, `scoreimdb`, `status`, `type`) VALUES ('1', '2023', 'Transformers: Quái Thú Trỗi Dậy dựa trên sự kiện Beast Wars trong loạt phim hoạt hình \"Transformers\", nơi mà các robot có khả năng biến thành động vật khổng lồ cùng chiến đấu chống lại một mối đe dọa tiềm tàng.', '127', '../assets/client/img/poster/transformers-quai-thu-troi-day.jpg', 'TRANSFORMERS: QUÁI THÚ TRỖI DẬY', 'HD_VIET_SUB', '6.4', 'COMPLETE', 'MOVIE');
INSERT INTO `movie_project`.`movie` (`id`, `aired_year`, `description`, `duration`, `poster`, `name`, `quality`, `scoreimdb`, `status`, `type`) VALUES ('2', '2023', 'Vệ Binh Dải Ngân Hà 3 kể về Peter Quill, vẫn quay cuồng vì mất Gamora, phải tập hợp đội của mình xung quanh anh ta để bảo vệ vũ trụ cùng với việc bảo vệ một người trong số họ. Nếu bất kì một nhiệm vụ nào không hoàn thành xuất sắc, nó hoàn toàn có thể dẫn đến sự kết thúc của những Vệ Binh Giải Ngân Hà.', '149', '../assets/client/img/poster/ve-binh-dai-ngan-ha-3.jpg', 'VỆ BINH DẢI NGÂN HÀ 3', 'HD_VIET_SUB', '8.1', 'COMPLETE', 'MOVIE');
INSERT INTO `movie_project`.`movie` (`id`, `aired_year`, `description`, `duration`, `poster`, `name`, `quality`, `scoreimdb`, `status`, `type`) VALUES ('3', '2023', 'Người Nhện: Du Hành Vũ Trụ kể về Sau khi gặp lại Gwen Stacy, chàng Spider-Man thân thiện đến từ Brooklyn phải du hành qua đa vũ trụ và gặp một nhóm Người Nhện chịu trách nhiệm bảo vệ các vũ trụ song song. Nhưng khi nhóm siêu anh hùng xung đột về cách xử lý một mối đe dọa mới, Miles buộc phải đọ sức với các Người Nhện khác và phải xác định lại ý nghĩa của việc trở thành một người hùng để có thể cứu những người cậu yêu thương nhất.', '140', '../assets/client/img/poster/nguoi-nhen-du-hanh-vu-tru.jpg', 'NGƯỜI NHỆN: DU HÀNH VŨ TRỤ', 'HD_VIET_SUB', '8.9', 'COMPLETE', 'MOVIE');
INSERT INTO `movie_project`.`movie` (`id`, `aired_year`, `description`, `duration`, `poster`, `name`, `quality`, `scoreimdb`, `status`, `type`) VALUES ('4', '2023', 'Khế Ước kể về trong nhiệm vụ cuối cùng ở Afghanistan, Trung sĩ John Kinley cùng đội với phiên dịch viên bản địa Ahmed. Khi đơn vị của họ bị phục kích, Kinley và Ahmed là 2 người sống sót duy nhất. Bị kẻ địch truy đuổi, Ahmed liều mạng đưa Kinley đang bị thương băng qua nhiều dặm đường địa hình khắc nghiệt đến nơi an toàn. Trở về Mỹ, Kinley biết rằng Ahmed và gia đình không dc cấp giấy thông hành tới Mỹ như đã hứa. Quyết tâm bảo vệ bạn mình và đền ơn cứu mạng, Kinley trở lại chiến trường để giúp Ahmed và gia đình trước khi lực lượng phiến quân phát hiện ra họ.', '123', '../assets/client/img/poster/khe-uoc.jpg', 'KHẾ ƯỚC', 'HD_VIET_SUB', '7.5', 'COMPLETE', 'MOVIE');
INSERT INTO `movie_project`.`movie` (`id`, `aired_year`, `description`, `duration`, `poster`, `name`, `quality`, `scoreimdb`, `status`, `type`) VALUES ('5', '2023', 'The Flash bắt đầu khi Barry Allen (Ezra Miller thủ vai) sử dụng siêu năng lực của mình để du hành thời gian nhằm thay đổi những sự kiện trong quá khứ. Nhưng khi nỗ lực cứu lấy gia đình mình vô tình thay đổi tương lai, Barry bị kẹt lại trong một thực tại, nơi tướng Zod tái xuất và đe dọa hủy diệt tất cả, nhưng không có bất cứ siêu anh hùng nào đứng ra giải cứu… Trừ khi Barry có thể thuyết phục một Batman rất khác và giải cứu một cư dân Kryptonian đang bị cầm tù… dẫu có thể đó không phải người mà anh thực sự tìm kiếm. Để giải cứu thế giới hiện tại cũng như trở lại tương lai mình từng biết, niềm hy vọng duy nhất của Barry là phải chạy đua vì cuộc đời mình. Vậy nhưng, sự hy sinh tuyệt đối đó có là đủ để đưa thế giới về lại như ban đầu?', '144', '../assets/client/img/poster/flash.jpg', 'FLASH', 'HD', '7.1', 'COMPLETE', 'MOVIE');
INSERT INTO `movie_project`.`movie` (`id`, `aired_year`, `description`, `duration`, `poster`, `name`, `quality`, `scoreimdb`, `status`, `type`) VALUES ('6', '2022', 'Khóa Chặt Cửa Nào Suzume kể về câu chuyện khi Suzume vô tình gặp một chàng trai trẻ đến thị trấn nơi cô sinh sống với mục đích tìm kiếm \"một cánh cửa\". Để bảo vệ Nhật Bản khỏi thảm họa, những cánh cửa rải rác khắp nơi phải được đóng lại, và bất ngờ thay Suzume cũng có khả năng đóng cửa đặc biệt này. Từ đó cả hai cùng nhau thực hiện sự mệnh \"khóa chặt cửa\"!', '122', '../assets/client/img/poster/khoa-chat-cua-nao-suzume.jpg', 'KHÓA CHẶT CỬA NÀO SUZUME', 'HD_VIET_SUB', '7.7', 'COMPLETE', 'MOVIE');
INSERT INTO `movie_project`.`movie` (`id`, `aired_year`, `description`, `duration`, `poster`, `name`, `quality`, `scoreimdb`, `status`, `type`) VALUES ('7', '2022', 'Sword Art Online the Movie: Progressive - Scherzo of Deep Night kể về hai tháng sau khi trò chơi chết chóc bắt đầu, Kirito và Asuna liên tiếp gặt hái những thành công mới. Trong một lần săn kho báu, họ phải đối mặt với con quái vật mà Asuna kinh hãi nhất.', '101', '../assets/client/img/poster/sword-art-online-the-movie-progressive-scherzo-of-deep-night.jpg', 'SWORD ART ONLINE THE MOVIE: PROGRESSIVE - SCHERZO OF DEEP NIGHT', 'HD_VIET_SUB', '7.7', 'COMPLETE', 'MOVIE');
INSERT INTO `movie_project`.`movie` (`id`, `aired_year`, `description`, `duration`, `poster`, `name`, `quality`, `scoreimdb`, `status`, `type`) VALUES ('8', '2023', 'Quá Nhanh Quá Nguy Hiểm 10 kể về Dom Toretto và gia đình của anh ấy bị trở thành mục tiêu của người con trai đầy thù hận của ông trùm ma túy Hernan Reyes.', '141', '../assets/client/img/poster/qua-nhanh-qua-nguy-hiem-10.jpg', 'QUÁ NHANH QUÁ NGUY HIỂM 10', 'HD', '5.9', 'COMPLETE', 'MOVIE');
INSERT INTO `movie_project`.`movie` (`id`, `aired_year`, `description`, `duration`, `poster`, `name`, `quality`, `scoreimdb`, `status`, `type`) VALUES ('9', '2023', 'Nàng Tiên Cá kể về câu chuyện được yêu thích về Ariel - một nàng tiên cá trẻ xinh đẹp và mạnh mẽ với khát khao phiêu lưu. Ariel là con gái út của Vua Triton và cũng là người ngang ngạnh nhất, nàng khao khát khám phá về thế giới bên kia đại dương. Trong một lần ghé thăm đất liền, nàng đã phải lòng Hoàng tử Eric bảnh bao. Trong khi tiên cá bị cấm tiếp xúc với con người, Ariel đã làm theo trái tim mình. Nàng đã thỏa thuận với phù thủy biển Ursula hung ác để cơ hội sống cuộc sống trên đất liền. Nhưng cuối cùng việc này lại đe dọa tới mạng sống của Ariel và vương miện của cha nàng.', '135', '../assets/client/img/poster/nang-tien-ca.jpg', 'NÀNG TIÊN CÁ', 'CAM', '7.2', 'COMPLETE', 'MOVIE');
INSERT INTO `movie_project`.`movie` (`id`, `aired_year`, `description`, `duration`, `poster`, `name`, `quality`, `scoreimdb`, `status`, `type`) VALUES ('10', '2023', 'Ngục Tối & Rồng: Danh Dự Của Kẻ Trộm kể về một tên trộm quyến rũ và một nhóm những kẻ bịp bợm nghiệp dư thực hiện vụ trộm sử thi nhằm lấy lại một di vật đã mất, nhưng mọi thứ trở nên nguy hiểm khó lường hơn bao giờ hết khi họ đã chạm trán nhầm người. Ngục Tối Và Rồng: Danh Dự Của Kẻ Trộm mang thế giới huyền ảo của trò chơi nhập vai huyền thoại lên màn ảnh rộng bằng một cuộc phiêu lưu tràn ngập các màn hành động đã mắt cùng màu sắc hài hước, vui nhộn.', '134', '../assets/client/img/poster/nguc-toi-rong-danh-du-cua-ke-trom.jpg', 'NGỤC TỐI & RỒNG: DANH DỰ CỦA KẺ TRỘM\n', 'HD_VIET_SUB', '7.3', 'COMPLETE', 'MOVIE');
INSERT INTO `movie_project`.`movie` (`id`, `aired_year`, `description`, `duration`, `poster`, `name`, `quality`, `scoreimdb`, `status`, `type`) VALUES ('11', '2023', 'Sát Thủ John Wick 4 là câu chuyện với cái giá phải trả ngày càng tăng, John Wick tham gia cuộc chiến chống lại High Table trên toàn cầu khi tìm kiếm những người chơi quyền lực nhất trong thế giới ngầm, từ New York qua Paris, Osaka đến cả Berlin.', '160', '../assets/client/img/poster/sat-thu-john-wick-4-11119.jpg', 'SÁT THỦ JOHN WICK 4', 'HD_VIET_SUB', '7.8', 'COMPLETE', 'MOVIE');
INSERT INTO `movie_project`.`movie` (`id`, `aired_year`, `description`, `duration`, `poster`, `name`, `quality`, `status`, `type`) VALUES ('12', '2023', 'Trần Duyên kể về câu chuyện xoay quanh Kỷ Nhược Trần, là một tiểu tử bồi bàn của một khách sạn bí ẩn có cái tên Long Môn. Nhờ có tư chất thông minh, không hiểu vì cố ý hay vô tình mà hắn bị cả tu chân giới nhận nhầm là Trích Tiên (Tiên nhân xuống trần gian), là đối tượng mà cả tu chân giới thèm thuồng tranh đoạt. Chính nhờ cơ duyên này, hắn mơ hồ bước vào con đường đại đạo, từng bước vấn đỉnh thế giới tu chân kỳ ảo.', '45', 'tran-duyen.jpg', 'TRẦN DUYÊN', 'HD', 'COMPLETE', 'SERIES');
